let results = [];
const items = $("On Form Submission").all()

for (item of items) {
    for (key of Object.keys(item.binary)) {
        results.push({
            json: {
                fileName: item.binary[key].fileName
            },
            binary: {
                data: item.binary[key],
            }
        });
    }
}

return results;

const url = $input.first().json['BG Remove Image']

// Extract the ID using a regular expression
const match = url.match(/\/d\/([^/]+)/);

return {
  json: {
    fileId: match ? match[1] : null,
  },
};

const title = $json["Product Title"] || "default";
const slug = title.toLowerCase().replace(/\s+/g, '-') + '-' + Math.random().toString(36).substring(2, 8);
return { slug };
