// Format movie recommendations from genre search
const movies = items[0].json.results.slice(0, 5); // Top 5 movies
const genre = $node["Extract Genre"].json.genre;
const phoneNumber = items[0].json.phone_number;

let message = `üé¨ *${genre.toUpperCase()} Movie Recommendations:*\n\n`;

movies.forEach((movie, index) => {
  const rating = movie.vote_average ? `‚≠ê ${movie.vote_average}/10` : 'No rating';
  const year = movie.release_date ? `(${movie.release_date.split('-')[0]})` : '';

  message += `${index + 1}. *${movie.title}* ${year}\n`;
  message += `${rating}\n`;
  if (movie.overview && movie.overview.length > 100) {
    message += `${movie.overview.substring(0, 100)}...\n\n`;
  } else if (movie.overview) {
    message += `${movie.overview}\n\n`;
  } else {
    message += 'No description available\n\n';
  }
});

message += `üí° *Want streaming info for any movie? Just ask "Where can I watch [Movie Name]?"*`;

return [{
  json: {
    phone_number: phoneNumber,
    message: message,
    type: 'genre_recommendations'
  }
}];

// Format streaming availability response
const movie = $node["Search Specific Movie"].json.results[0];
const streamingData = items[0].json;
const phoneNumber = $node["Extract Movie Title"].json.phone_number;

if (!movie) {
  return [{
    json: {
      phone_number: phoneNumber,
      message: "‚ùå Sorry, I couldn't find that movie. Please check the spelling and try again.",
      type: 'error'
    }
  }];
}

let message = `üé¨ *${movie.title}*`;
if (movie.release_date) {
  message += ` (${movie.release_date.split('-')[0]})`;
}
message += `\n‚≠ê ${movie.vote_average}/10\n\n`;

if (movie.overview) {
  message += `üìñ *Plot:*\n${movie.overview}\n\n`;
}

// Check if streaming data is available
if (streamingData.sources && streamingData.sources.length > 0) {
  message += `üì∫ *Available on:*\n`;
  streamingData.sources.slice(0, 8).forEach(source => {
    const platform = source.name || source.source_name || 'Unknown Platform';
    const type = source.type === 'sub' ? '(Subscription)' : 
                source.type === 'rent' ? '(Rent)' : 
                source.type === 'buy' ? '(Buy)' : '';
    message += `‚Ä¢ ${platform} ${type}\n`;
  });
} else {
  message += `üì∫ *Streaming Info:*\n‚Ä¢ Check Netflix, Prime Video, Disney+, or other platforms\n‚Ä¢ May be available for rent/purchase on digital platforms`;
}

return [{
  json: {
    phone_number: phoneNumber,
    message: message,
    type: 'streaming_info'
  }
}];

// Prepare WhatsApp message payload
const phoneNumber = items[0].json.phone_number;
const message = items[0].json.message;

return [{
  json: {
    messaging_product: "whatsapp",
    to: phoneNumber,
    type: "text",
    text: {
      body: message
    }
  }
}];
