parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "candidate_strengths": {
      "type": "array",
      "items": { "type": "string" },
      "description": "List of candidate's key strengths with evidence"
    },
    "candidate_weaknesses": {
      "type": "array", 
      "items": { "type": "string" },
      "description": "List of areas where candidate falls short"
    },
    "risk_assessment": {
      "type": "object",
      "properties": {
        "level": {
          "type": "string",
          "enum": ["Low", "Medium", "High"]
        },
        "explanation": {
          "type": "string",
          "description": "Detailed risk analysis"
        }
      }
    },
    "opportunity_assessment": {
      "type": "object",
      "properties": {
        "level": {
          "type": "string", 
          "enum": ["Low", "Medium", "High"]
        },
        "explanation": {
          "type": "string",
          "description": "Upside potential analysis"
        }
      }
    },
    "overall_score": {
      "type": "integer",
      "minimum": 1,
      "maximum": 10,
      "description": "Overall fit score 1-10"
    },
    "score_justification": {
      "type": "string",
      "description": "Detailed explanation of the score"
    },
    "next_steps_recommendation": {
      "type": "string",
      "description": "Recommended next actions"
    }
  },
  "required": ["candidate_strengths", "candidate_weaknesses", "risk_assessment", "opportunity_assessment", "overall_score", "score_justification", "next_steps_recommendation"]
}
