let idx = 0
let alerts = []

for (const item of $input.all()) {
  for (const alert of item.json.alerts) {
    idx++

      let template = `Alert ${idx}:
  **Headline:** ${alert.properties.headline}
  **Severity:** ${alert.properties.severity}
  **Description:** ${alert.properties.description}
  **Instructions:** ${alert.properties.instruction}
`

    alerts.push(template)
  }

  item.json = { nws_alerts: alerts.join('\n\n'), nws_alerts_count: alerts.length }
}

return $input.all();
