parameters.jsCode:
// Extract input data
const items = $input.all();
let results = [];
let allQuestions = new Set();

// Collect all possible question fields dynamically
items.forEach(item => {
    let output = item.json.output;
    let questions = output.questions;

    Object.keys(questions).forEach(q => allQuestions.add(q));
});

// Convert to array format
items.forEach(item => {
    let output = item.json.output;
    let row = {
        "Candidate File Name": output.candidate_file_name,
        "Candidate Name": output.candidate_name
    };

    // Add all dynamically available question scores
    allQuestions.forEach(question => {
        row[question] = output.questions[question] || "";
    });

    // Add overall points
    row["Overall Points"] = output.overall_points;
    results.push(row);
});

return results.map(row => ({ json: row }));

