const text = $('Webhook - FB Messenger').first().json.body.entry[0].messaging[0].message.text;
const lines = text.split(/\r?\n/);
const result = {};
lines.forEach(line => {
  const [key, ...rest] = line.split(':');
  if (key && rest.length) {
    result[key.trim().replace(/^[^a-zA-Z0-9]+/g, '')] = rest.join(':').trim();
  }
});
return [{ json: result }];
