const data = $input.first().json.data

const regex = /https?:\/\/[^\/]+/g

const urls = data.match(regex)

return urls.map(url => ({json: {url: url}}))

let raw = $json.output;

// Remove ```json or ``` wrapping if it exists
raw = raw.replace(/^```json\s*|\s*```$/g, '').trim();

let outputJson;

try {
  outputJson = JSON.parse(raw);
} catch (err) {
  outputJson = { error: 'Invalid JSON format in promptResult' };
}

return {
  json: outputJson
};

let dontExist = true;

// Get reference domain from 'Loop over URLs' node
const referenceUrl = $('Loop over URLs').first().json.url;
const referenceDomain = referenceUrl.match(/^https?:\/\/([^\/?#]+)(?:[\/?#]|$)/i)?.[1]?.toLowerCase();

if (!referenceDomain) {
  throw new Error('Invalid reference URL');
}

// Loop over input items
for (const item of $input.all()) {
  const urls = item.json.website_url;

  for (const url of urls) {
    const domain = url.match(/^https?:\/\/([^\/?#]+)(?:[\/?#]|$)/i)?.[1]?.toLowerCase();
    if (!domain) continue;

    if (domain === referenceDomain) {
      dontExist = false;
      break;
    }
  }

  if (!dontExist) break;
}

// Return result
return {
  json: {
    dontExist
  }
};
