/************************************************************
 * Build Apollo People-search URL from parser output
 * Handles both shapes:
 *   { query: [ { ‚Ä¶ } ] }      ‚Üê array
 *   { query: { ‚Ä¶ } }          ‚Üê object
 ************************************************************/

// 1. Obtener el JSON que llega del nodo anterior
const inputData = $json;

// 2. Normalizar para que paramsData siempre sea un objeto
let paramsData;

if (Array.isArray(inputData.query)) {
  // Caso antiguo: query es un array
  paramsData = inputData.query[0];
} else if (inputData.query) {
  // Caso nuevo: query es un objeto
  paramsData = inputData.query;
} else {
  throw new Error('Falta la propiedad "query" en el input');
}

// 3. URL base de Apollo (interfaz web)
//    Para llamar a la API REST cambia a https://api.apollo.io/v1/mixed_people/search
const baseURL = 'https://app.apollo.io/#/people';

// 4. Construir los par√°metros din√°micos
const queryParts = [
  'sortByField=recommendations_score',
  'sortAscending=false',
  'page=1',
];

// Helper para arrays ‚Üí personTitles[]=ceo
const addArrayParams = (paramName, values) => {
  values.forEach((val) => {
    const decoded = val.replace(/\+/g, ' ');          // ‚Äúbarcelona+spain‚Äù ‚áí ‚Äúbarcelona spain‚Äù
    queryParts.push(`${paramName}[]=${encodeURIComponent(decoded)}`);
  });
};

// job_title ‚Üí personTitles[]
if (Array.isArray(paramsData.job_title)) {
  addArrayParams('personTitles', paramsData.job_title);
}

// location ‚Üí personLocations[]
if (Array.isArray(paramsData.location)) {
  addArrayParams('personLocations', paramsData.location);
}

// business ‚Üí qOrganizationKeywordTags[]
if (Array.isArray(paramsData.business)) {
  addArrayParams('qOrganizationKeywordTags', paramsData.business);
}

// campos est√°ticos
queryParts.push('includedOrganizationKeywordFields[]=tags');
queryParts.push('includedOrganizationKeywordFields[]=name');

// 5. Montar la URL final
const finalURL = `${baseURL}?${queryParts.join('&')}`;

// 6. Devolver resultado
return [{ json: { finalURL } }];
