// Get the voice-refined content
const refinedPost = $input.first().json.message.content;

console.log("Final post:", refinedPost);
console.log("Character count:", refinedPost.length);

// Simple scoring function
function scorePost(post) {
  let score = 0;

  // Length optimization
  if (post.length >= 800 && post.length <= 1300) score += 3;
  else if (post.length >= 500 && post.length <= 1500) score += 2;
  else score += 1;

  // Engagement elements
  if (post.includes('?')) score += 2;
  if (post.match(/\d+/g)) score += 2; // Contains numbers
  if (post.includes('Here\'s the thing')) score += 2;
  if (post.includes(':')) score += 1;

  // Voice authenticity markers
  if (post.includes('n8n') || post.includes('OpenAI') || post.includes('Mydra')) score += 1;
  if (post.includes('minutes') || post.includes('hours')) score += 1;

  // Penalties for AI-speak
  if (post.includes('game-changer') || post.includes('unlock')) score -= 3;

  return Math.max(0, score);
}

const score = scorePost(refinedPost);
const timestamp = new Date().toISOString();

// Simple status logic
let status;
if (score >= 7) status = 'pending_review';
else if (score >= 5) status = 'needs_editing';
else status = 'needs_major_revision';

// Return only the new scoring data
return {
  json: {
    generated_post: refinedPost,
    ai_score: score,
    character_count: refinedPost.length,
    status: status,
    human_approved: false,
    generated_at: timestamp
  }
};

const imagePrompt = $input.first().json.message.content

// Clean and escape the prompt for JSON
const cleanPrompt = imagePrompt
  .replace(/"/g, '\\"')  // Escape quotes
  .replace(/\n/g, ' ')   // Remove line breaks
  .replace(/\r/g, ' ')   // Remove carriage returns
  .trim();               // Remove extra whitespace

return {
  json: {
    clean_prompt: cleanPrompt
  }
};
