// Get the input item (using $input.item for single item processing is fine)
const item = $input.item;

// --- Define the Expected Binary Property Name ---
// This is crucial. If the node feeding this Code node outputs binary data
// under a different property name (e.g., 'file', 'binaryOutput'),
// you MUST change this value accordingly.
const binaryPropertyName = 'data';

// --- Check if any binary data exists and if the specific property is present ---
if (!item.binary || !item.binary[binaryPropertyName]) {
  const availableProperties = item.binary ? Object.keys(item.binary).join(', ') : 'none';
  throw new Error(`Binary property '${binaryPropertyName}' not found on input item. Available binary properties: ${availableProperties}. Check the 'Property Name' setting in the preceding node if it handles binary data (e.g., HTTP Request, Read File).`);
}

// --- Access Binary Data and Calculate Size using Helpers ---
// Use the n8n helper function to get the buffer
const buffer = await this.helpers.getBinaryDataBuffer(0, binaryPropertyName);

// The length of the buffer *is* the size in bytes
const fileSizeInBytes = buffer.length;

// --- Prepare Output JSON ---
const outputJson = {
  fileSizeInBytes: fileSizeInBytes,
};

return outputJson;
