const MAX_LEN = 2000;

// ---- 0) L·∫•y text t·ª´ AI Agent ----
let text = $input.first().json.output.message.trim();
if (!text) return [];

// ---- 1) Chu·∫©n ho√°: b·ªè Markdown & HTML ----
// B·ªè code block ```
text = text.replace(/```[\s\S]*?```/g, '');
// Inline code `
text = text.replace(/`([^`]+)`/g, '$1');
// Bold, italic, underline, strike
text = text.replace(/(\*{1,2}|_{1,2}|~~)(.*?)\1/g, '$2');
// Header #, blockquote >
text = text.replace(/^\s{0,3}#{1,6}\s+/gm, '');
text = text.replace(/^\s{0,3}>\s?/gm, '');
// Link [text](url) -> text
text = text.replace(/\[([^\]]+)\]\(([^)]+)\)/g, '$1');
// Image ![alt](url) -> alt
text = text.replace(/!\[([^\]]*)\]\([^)]+\)/g, '$1');
// Xo√° HTML tag
text = text.replace(/<[^>]+>/g, '');

// Gi·ªØ bullet list: *, +, - -> -
text = text.replace(/^\s*[\*\+]\s+/gm, '- ');

// Chu·∫©n ho√° xu·ªëng d√≤ng
text = text.replace(/\r\n?/g, '\n');
text = text.replace(/[ \t]+\n/g, '\n');
text = text.replace(/\n{3,}/g, '\n\n').trim();

// ---- 2) H√†m c·∫Øt th√¥ng minh ----
function splitSmart(input, maxLen) {
  const chunks = [];
  let remaining = input;

  while (remaining && chunks.length < 10) {
    if (remaining.length <= maxLen) {
      chunks.push(remaining.trim());
      break;
    }

    // ∆Øu ti√™n c·∫Øt ·ªü newline g·∫ßn maxLen
    let idx = remaining.lastIndexOf('\n', maxLen);
    if (idx < maxLen * 0.6) {
      // Th·ª≠ c·∫Øt ·ªü d·∫•u c√¢u
      const look = remaining.slice(0, Math.min(remaining.length, maxLen + 200));
      const punct = Math.max(
        look.lastIndexOf('. '),
        look.lastIndexOf('! '),
        look.lastIndexOf('? ')
      );
      if (punct > 0) idx = punct + 1;
    }
    if (idx < maxLen * 0.6) {
      // Th·ª≠ c·∫Øt ·ªü kho·∫£ng tr·∫Øng
      const ws = remaining.lastIndexOf(' ', maxLen);
      idx = ws > 0 ? ws : maxLen;
    }

    chunks.push(remaining.slice(0, idx).trim());
    remaining = remaining.slice(idx).trim();
  }

  return chunks;
}

// ---- 3) Xu·∫•t k·∫øt qu·∫£ ----
const chunks = splitSmart(text, MAX_LEN);
return chunks.map(c => ({ json: { text: c } }));
