// Loop over input items and add a new field called 'myNewField' to the JSON of each one
var outitem = [];
for (var item of $input.all()) {
  for (const subitem of item.json.data) {
    outitem.push({
      "gid": subitem.gid,
      "name": subitem.name
    });
  }
}

return outitem;
