const items = $input.all();
const mergedData = [];

for (const item of items) {
  const platform = item.json.platform || 'unknown';
  const cost = item.json.cost || item.json.totalCost || 0;
  const resourceId = item.json.resourceId || item.json.service || 'unknown';

  mergedData.push({
    platform,
    cost: parseFloat(cost),
    resourceId,
    timestamp: new Date().toISOString(),
    owner: item.json.tags?.owner || item.json.owner || 'untagged'
  });
}

return mergedData.map(data => ({ json: data }));

const items = $input.all();
const alerts = [];
const COST_THRESHOLD = parseFloat($vars.COST_THRESHOLD || '50');
const SPIKE_MULTIPLIER = parseFloat($vars.SPIKE_MULTIPLIER || '2.0');

for (const item of items) {
  const cost = item.json.cost;
  const platform = item.json.platform;
  const resourceId = item.json.resourceId;

  // Simple spike detection - compare with threshold
  if (cost > COST_THRESHOLD) {
    alerts.push({
      type: 'threshold_exceeded',
      platform,
      resourceId,
      currentCost: cost,
      threshold: COST_THRESHOLD,
      owner: item.json.owner,
      severity: cost > (COST_THRESHOLD * 2) ? 'HIGH' : 'MEDIUM'
    });
  }
}

return alerts.map(alert => ({ json: alert }));

// Auto-tagging logic based on platform
const item = $input.first().json;
const platform = item.platform;
const resourceId = item.resourceId;

let tagCommand = '';

switch(platform) {
  case 'aws':
    tagCommand = `aws ec2 create-tags --resources ${resourceId} --tags Key=CostAlert,Value=true Key=AlertTime,Value=${new Date().toISOString()}`;
    break;
  case 'azure':
    tagCommand = `az resource tag --ids ${resourceId} --tags CostAlert=true AlertTime=${new Date().toISOString()}`;
    break;
  case 'gcp':
    tagCommand = `gcloud compute instances add-labels ${resourceId} --labels=cost-alert=true,alert-time=${Date.now()}`;
    break;
}

return [{ json: { ...item, tagCommand } }];
