// This function will iterate through your data and reformat it
// into a list of single-item lists.
const refinedOutput = $input.first().json.output.refined_output
const processedItems = [];

for (const item of refinedOutput) {
  // Create a new item object with a 'json' key
  // that holds the object directly.
  const newItem = {
    json: item,
  };
  processedItems.push(newItem);
}

// Return the new list of items
return processedItems;
