const items = $input.all();
return items.map(item => {
  const viewCount = parseInt(String(item.json?.viewCount || '0'), 10);
  const likeCount = parseInt(String(item.json?.likeCount || '0'), 10);
  const commentCount = parseInt(String(item.json?.commentCount || '0'), 10);


  let performance = 0;

  if (viewCount > 0) {
    // Calculate engagement rate and scale by 10x for more realistic YouTube scores
    const engagementRate = ((likeCount + commentCount) / viewCount) * 1000; // 10x boost
    performance = Math.min(Math.max(engagementRate, 0), 100);
  }

  const roundedPerformance = Math.round(performance);

  let performanceText = "üíÄ Dead";

  // Adjusted thresholds for realistic YouTube engagement rates (much stricter)
  if (roundedPerformance >= 80) {
    performanceText = "üöÄ HOLY HELL"; // 8%+ engagement = truly exceptional/viral
  } else if (roundedPerformance >= 60) {
    performanceText = "üî• INSANE"; // 6%+ engagement = insane performance
  } else if (roundedPerformance >= 40) {
    performanceText = "üí™ CRUSHING IT"; // 4%+ engagement = crushing it
  } else if (roundedPerformance >= 30) {
    performanceText = "‚≠ê Stellar"; // 3%+ engagement = stellar
  } else if (roundedPerformance >= 20) {
    performanceText = "üí™ Strong"; // 2%+ engagement = strong
  } else if (roundedPerformance >= 15) {
    performanceText = "üòä Good"; // 1.5%+ engagement = good
  } else if (roundedPerformance >= 10) {
    performanceText = "üôÇ Decent"; // 1%+ engagement = decent
  } else if (roundedPerformance >= 5) {
    performanceText = "üòê Average"; // 0.5%+ engagement = average
  } else {
    // Use switch for very low scores (0-4%)
    switch(roundedPerformance) {
      case 0: performanceText = "üíÄ Dead"; break;
      case 1: performanceText = "üò¥ Sleeping"; break;
      case 2: performanceText = "üòê Meh"; break;
      case 3: performanceText = "üòï Not good"; break;
      case 4: performanceText = "üòû Poor"; break;
      default: performanceText = "üíÄ Dead";
    }
  }
  $input.first().json.performance = performance;
  $input.first().json.performanceText = performanceText;


  return {
    ...$json
  };
});

