const items = $input.all();
const updatedItems = items.map((item) => {
  if (
    item.json &&
    item.json.output &&
    typeof item.json.output.post === "string"
  ) {
    item.json.output.post = item.json.output.post.replace(/\n/g, "\\n");
  }
  return item;
});
return updatedItems;
