const data = []
for (const item of $input.all()) {
  data.push({
    date: `Date: ${item.json.Date}`,
    newLeads: `New Leads: ${item.json['New Leads']}`,
    callsMade: `Calls Made: ${item.json['Calls Made']}`,
    demosBooked: `Demos Booked: ${item.json['Demos Booked']}`,
    dealsClosed: `Deals Closed: ${item.json['Deals Closed']}`,
    revenue: `Revenue in $: ${item.json['Revenue ($)']}`
  })
}

return [
  {
    json: {
      lastDate:$('Webhook').first().json.body.date,
      data
    }
  }
]
