/**
 * n8n Code node (JavaScript)
 * Input : items dari HTTP Request (Perplexity chat/completions)
 * Output: HANYA kolom spreadsheet (tanpa meta _sources/_confidence/_notes/_raw_enriched_json)
 */

function pickBody(payload) {
  // HTTP node kadang return "Full Response" (punya key 'body')
  if (payload && typeof payload === 'object' && payload.body !== undefined) {
    return payload.body;
  }
  return payload;
}

function firstOrSelf(v) {
  return Array.isArray(v) ? (v[0] ?? null) : v;
}

function extractContent(perp) {
  const c =
    perp?.choices?.[0]?.message?.content ??
    perp?.data?.choices?.[0]?.message?.content ??
    null;

  if (typeof c !== 'string') {
    throw new Error('Perplexity response tidak berisi choices[0].message.content (string).');
  }

  // bersihin kemungkinan code fences ```json ... ```
  const cleaned = c.trim()
    .replace(/^```(?:json)?\s*/i, '')
    .replace(/```$/i, '')
    .trim();

  let obj;
  try {
    obj = JSON.parse(cleaned);
  } catch (e) {
    throw new Error('Gagal parse JSON dari Perplexity content: ' + e.message + ' | content=' + cleaned.slice(0, 300));
  }
  return obj;
}

function toSheetRow(enriched) {
  // Balikin HANYA kolom yang ada di sheet
  return {
    "row_number": enriched?.row_number ?? null,
    "Place Id": enriched?.place_id ?? null,
    "Title": enriched?.title ?? null,
    "Category": enriched?.category ?? null,
    "City": enriched?.city ?? null,
    "State": enriched?.state ?? null,
    "Address": enriched?.address ?? null,
    "Company name": (enriched?.company_name ?? null) || null,
    "Website": enriched?.website ?? null,
    "Email": enriched?.email ?? null,
    "Phone Number": enriched?.phone_number ?? null,
    "Instagram": enriched?.instagram ?? null,
    "Facebook": enriched?.facebook ?? null,
    "Linkedin": enriched?.linkedin ?? null,
    "Review Count": enriched?.review_count ?? null,
    "Average Stars": enriched?.average_stars ?? null,
    "enriched": enriched?.enriched ?? "yes"
  };
}

const results = [];

for (const item of items) {
  try {
    // 1) Ambil payload utama
    let payload = pickBody(item.json);

    // 2) Kalau string, coba parse JSON dulu
    if (typeof payload === 'string') {
      try { payload = JSON.parse(payload); } catch (_) { /* biarin */ }
    }

    // 3) Ambil objek pertama kalau payload array
    payload = firstOrSelf(payload);

    if (!payload || typeof payload !== 'object') {
      throw new Error('Payload kosong atau bukan object.');
    }

    // 4) Ekstrak JSON dari message.content
    const enriched = extractContent(payload);

    // 5) Map ke kolom sheet (tanpa meta)
    const row = toSheetRow(enriched);

    results.push({ json: row });
  } catch (err) {
    results.push({
      json: {
        _error: true,
        _message: String(err?.message || err)
      }
    });
  }
}

return results;
