// Get the MR description from the previous node's input
const description = $input.first().json.description
// Regex to find a JIRA issue key (e.g., PROJ-123).
// The \b ensures it matches a whole word.
const jiraRegex = /\b([A-Z]+-\d+)\b/;
const match = description.match(jiraRegex);

// If a key is found, return it.
if (match && match[0]) {
  // We name the output 'jiraIssueKey' for clarity.
  return [{
    json: {
      jiraIssueKey: match[0]
    }
  }];
} else {
  // Return an empty array to stop this path if no key is found.
  return [];
}

