// Define the agent's author_id (update this to match your system)
const supportStaffId = 19780258553884;

// Get the input data (from previous node)
const comments = items[0].json.comments;
const subject = items[0].json.subject;
const ticketId = $('Fetch Zendesk ticket').first().json.ticket.id

// Filter only public comments
const publicComments = comments.filter(comment => comment.public === true && comment.type === 'Comment');

// Build the conversation array
const conversation = publicComments.map(comment => {
    return {
        role: comment.author_id === supportStaffId ? 'support_agent' : 'customer',
        message: comment.body
    };
});

// Return the final structured output
return [
    {
        json: {
            subject: subject,
            conversation: conversation,
            ticketId: ticketId
        }
    }
];

const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/;

// Get tags from the Zendesk ticket
const tags = $('Fetch Zendesk ticket').first().json.ticket.tags || [];

// Find the first tag that matches the UUID format
const topicId = tags.find(tag => uuidRegex.test(tag)) || null;

// Return with new field
return [
  {
    json: {
      ...items[0].json,
      topicId: topicId
    }
  }
];

// Get topicId from tags[0] in the other node
const topicId = $('Add Mintlify topicId to ticket tags').first().json.tags?.[0] || null;

// Get the current item (from "Parse conversation")
const item = $('Parse conversation').first().json.conversation

// Extract and simplify the conversation to only include message fields
const conversation = (item || []).map(entry => ({
  message: entry.message
}));

// Build and return the final output
return [
  {
    json: {
      conversation,
      topicId
    }
  }
];

