const supportAgentReplyCount = conversation.filter(msg => msg.role === 'support_agent').length;

// Return the count
return [
  {
    json: {
      aiReplyCount: supportAgentReplyCount
    }
  }
];

// Get the first item from input
const item = $input.first();

// Reverse the conversation array
const reversedConversation = $('Parse conversation').first().json.conversation.reverse();

// Return the modified object
return [
  {
    json: {
      conversation: reversedConversation,
      ticketId: item.json.ticketId
    }
  }
];

const response = $input.first().json.data
const [answer, citationsRaw] = response.split('||');
const citations = citationsRaw ? JSON.parse(citationsRaw) : [];

let final = answer;
if (citations.length > 0) {
  final += '\n\nüìö Related Docs:\n';
  citations.forEach((c, i) => {
    final += `${i + 1}. [${c.metadata.title}](https://docs.<YOUR-DOMAIN>.com${c.link})\n`;
  });
}

return {
  aiResponse: answer,
  citations,
  finalResponse: final
};

// Get the raw output string
const rawOutput = $input.first().json.output;

// Try to extract only the JSON part using a regex
const jsonMatch = rawOutput.match(/\{[\s\S]*\}/);

// If no match found, throw an error
if (!jsonMatch) {
  throw new Error('No valid JSON object found in the string.');
}

// Parse the matched JSON string
const parsed = JSON.parse(jsonMatch[0]);

// Ensure it's a plain object
if (typeof parsed !== 'object' || Array.isArray(parsed)) {
  throw new Error('Parsed JSON must be a plain object');
}

// Return parsed object
return [
  {
    json: parsed
  }
];
