// Process registration and generate unique details
const registration = $json;
const eventSettings = $node['Event Settings'].json;

// Generate unique registration ID
const registrationId = `REG_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

// Calculate reminder dates
const eventDate = new Date(eventSettings.eventDate);
const reminderDates = {
  weekBefore: new Date(eventDate.getTime() - 7 * 24 * 60 * 60 * 1000).toISOString(),
  dayBefore: new Date(eventDate.getTime() - 24 * 60 * 60 * 1000).toISOString(),
  twoHoursBefore: new Date(eventDate.getTime() - 2 * 60 * 60 * 1000).toISOString()
};

// Generate calendar event data
const calendarEvent = {
  summary: eventSettings.eventTitle,
  start: eventSettings.eventDate,
  end: eventSettings.eventEndDate,
  location: eventSettings.eventLocation,
  description: `Vous √™tes inscrit(e) √† ${eventSettings.eventTitle}. D√©tails: ${eventSettings.eventLocation}`
};

// Create access credentials for online events
const accessCredentials = {
  meetingId: eventSettings.eventLocation.includes('zoom') ? 'ID: 123 456 789' : null,
  accessCode: Math.random().toString(36).substr(2, 8).toUpperCase()
};

return {
  ...registration,
  registrationId,
  eventDetails: eventSettings,
  reminderDates,
  calendarEvent,
  accessCredentials,
  registeredAt: new Date().toISOString(),
  status: 'confirmed'
};
