// Input: per-day items from Edit Fields (each has externalKey, startDate, endDateExclusive)
// Output: one item per assignmentId with the set of current keys and a search window

const groups = {}; // { [assignmentId]: { currentKeys:Set, minDate, maxDate } }

for (const it of items) {
  const j = it.json;
  const parts = (j.externalKey || '').split(':'); // ["everhour", "<id>", "<date>"]
  const assignmentId = parts[1];
  if (!assignmentId) continue;

  if (!groups[assignmentId]) {
    groups[assignmentId] = {
      assignmentId,
      currentKeys: new Set(),
      minDate: j.startDate,
      maxDate: j.endDateExclusive
    };
  }
  groups[assignmentId].currentKeys.add(j.externalKey);
  if (j.startDate < groups[assignmentId].minDate) groups[assignmentId].minDate = j.startDate;
  if (j.endDateExclusive > groups[assignmentId].maxDate) groups[assignmentId].maxDate = j.endDateExclusive;
}

// Emit one item per assignment, with a small buffer on the search window (optional)
const out = [];
for (const id of Object.keys(groups)) {
  const g = groups[id];
  out.push({
    json: {
      assignmentId: id,
      currentKeys: Array.from(g.currentKeys),
      searchTimeMin: g.minDate + 'T00:00:00',
      searchTimeMax: g.maxDate + 'T23:59:59'
    }
  });
}
return out;

// Build the set of keys we SHOULD have right now
let minDate = null, maxDate = null;
const keys = new Set();

for (const it of items) {
  const j = it.json;
  if (j.externalKey) keys.add(j.externalKey);
  if (j.startDate && (!minDate || j.startDate < minDate)) minDate = j.startDate;
  if (j.endDateExclusive && (!maxDate || j.endDateExclusive > maxDate)) maxDate = j.endDateExclusive;
}

// Reasonable window (optional). Broaden if you want.
if (!minDate) minDate = new Date().toISOString().slice(0,10);
if (!maxDate) {
  const d = new Date(); d.setFullYear(d.getFullYear() + 1);
  maxDate = d.toISOString().slice(0,10);
}

return [{
  json: {
    currentKeys: Array.from(keys),
    searchTimeMin: minDate + 'T00:00:00',
    searchTimeMax: maxDate + 'T23:59:59'
  }
}];

// All time-off (including half-day) → keep creating as ALL-DAY events,
// but also expose `isAllDay` based on Everhour's original period.

function* eachDateInclusive(startStr, endStr, includeWeekends) {
  const start = new Date(startStr + 'T00:00:00');
  const end   = new Date(endStr   + 'T00:00:00');
  for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
    const dow = d.getDay(); // 0=Sun .. 6=Sat
    if (!includeWeekends && (dow === 0 || dow === 6)) continue;
    yield new Date(d);
  }
}
function fmtDate(d){ return d.toISOString().slice(0,10); }

const out = [];
for (const item of items) {
  const r = item.json;
  if ((r.status || '').toLowerCase() !== 'approved') continue;
  if (r.type !== 'time-off') continue;

  const userName = r.user?.name || 'Unknown';
  const userEmail = r.user?.email || '';
  const startDate = r.startDate;
  const endDate   = r.endDate || r.startDate;
  const includeWeekends = !!r.includeWeekends;
  const period = r.timeOffPeriod || 'full-day';   // 'full-day' | 'half-of-day'
  const type = r.timeOffType?.name || 'Time Off';
  const note = r.note || '';
  const id = r.id;

  // Derive the original "isAllDay" from Everhour's period (without changing mapping)
  const isAllDayOriginal = (period !== 'half-of-day');

  // Expand per-day; skip weekends if flag=false
  for (const d of eachDateInclusive(startDate, endDate, includeWeekends)) {
    const day = fmtDate(d);

    // All-day mapping (end date exclusive)
    const endObj = new Date(d);
    endObj.setDate(endObj.getDate() + 1);
    const endExclusive = fmtDate(endObj);

    out.push({
      json: {
        // You are still creating ALL-DAY events
        isAllDay: isAllDayOriginal,       // <-- added (reflects original Everhour period)
        startDate: day,
        endDateExclusive: endExclusive,

        employeeName: userName,
        employeeEmail: userEmail,
        type,
        note,
        status: r.status,

        // unique key per assignment-day
        externalKey: `everhour:${id}:${day}`,

        // keep for debugging/visibility
        originalPeriod: period
      }
    });
  }
}
return out;

// This Function is in "Run Once for Each Item" mode.
// $json here is the HTTP search response for THIS PTO item.

const ev = Array.isArray($json.items) ? $json.items[0] : null;

// IMPORTANT: use the current index, not 0
const i = $itemIndex;
const src = $items("Build Calendar Payload")[i].json;

return {
  json: {
    ...src,                              // keep your PTO fields for THIS item
    existingEventId: ev ? ev.id : null,  // id if found
    found: !!ev                          // boolean for IF node
  }
};

// Run Once for ALL Items
// Pair each HTTP Request2 result (events for an assignment) with the matching group from Code2

const groups = $items("Build Assignment Groups").map(it => it.json); // <-- node name must match exactly
const out = [];

for (let i = 0; i < items.length; i++) {
  const group = groups[i];
  if (!group) continue;

  const current = new Set(group.currentKeys);
  const apiItems = Array.isArray(items[i].json.items) ? items[i].json.items : [];

  for (const ev of apiItems) {
    const desc = ev.description || "";
    const m = desc.match(/Key:\s*(everhour:[^\s]+)/i); // "Key: everhour:<id>:<date>"
    if (!m) continue;
    const key = m[1];

    if (!current.has(key)) {
      out.push({
        json: { eventId: ev.id, key, assignmentId: group.assignmentId }
      });
    }
  }
}

return out; // one item per stale event to delete

// Run Once for ALL Items
// Compare calendar events (items[0].json.items) vs currentKeys from "Build global key set"

const current = new Set($items("Build Global Key Set")[0].json.currentKeys);
const evs = Array.isArray(items[0].json.items) ? items[0].json.items : [];
const out = [];

for (const ev of evs) {
  const desc = ev.description || "";
  const m = desc.match(/Key:\s*(everhour:[^\s]+)/i); // extracts "everhour:<id>:<date>"
  if (!m) continue;
  const key = m[1];
  if (!current.has(key)) {
    out.push({ json: { eventId: ev.id, key }});
  }
}
return out;  // one item per stale event to delete
