const lastModifiedStr = $input.item.json.last_modified;

let ageDays = 0;

if (lastModifiedStr && lastModifiedStr !== 'Unknown') {
  try {
    const lastModified = new Date(lastModifiedStr);
    const now = new Date();
    const diffMs = now.getTime() - lastModified.getTime();
    ageDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));
  } catch (error) {
    console.log('Error parsing date:', error);
    ageDays = 0;
  }
}

return [{
  ...$input.item.json,
  age_days: ageDays
}];

// Check if we should skip weekends
const includeWeekends = $('configuration sheet').item.json.INCLUDE_WEEKENDS;
const today = new Date();
const dayOfWeek = today.getDay(); // 0 = Sunday, 6 = Saturday

if (!includeWeekends && (dayOfWeek === 0 || dayOfWeek === 6)) {
  return {
    json: {
      ...$input.item.json,
      status: 'SKIPPED_WEEKEND',
      skip_reason: 'Weekend execution disabled'
    }
  };
}

return {
  json: {
    ...$input.item.json,
    status: 'PROCESSING'
  }
};

return {
  json: {
    ...$input.item.json,
    logged_reason: 'Execution skipped - weekend and INCLUDE_WEEKENDS is false'
  }
};
