// Extract the JSON URL from the Phantombuster output
const output = $input.first().json.output;

// More specific regex pattern to match the JSON result URL
const jsonUrlMatch = output.match(/JSON saved at (https:\/\/phantombuster\.s3\.amazonaws\.com\/\w+\/\w+\/result\.json)/);

if (jsonUrlMatch) {
  return [{ json: { resultUrl: jsonUrlMatch[1] } }];
} else {
  // If JSON URL not found, also try to find CSV URL as fallback
  const csvUrlMatch = output.match(/CSV saved at (https:\/\/phantombuster\.s3\.amazonaws\.com\/\w+\/\w+\/result\.csv)/);

  if (csvUrlMatch) {
    return [{ json: { resultUrl: csvUrlMatch[1] } }];
  } else {
    throw new Error('Could not find result URL in output. Output: ' + output);
  }
}

// Extract the JSON URL from the output
const output = $input.first().json.output;
const jsonUrlMatch = output.match(/JSON saved at (https:\/\/phantombuster\.s3\.amazonaws\.com\/[^\s]+\.json)/);

if (jsonUrlMatch) {
  return [{ json: { resultUrl: jsonUrlMatch[1] } }];
} else {
  throw new Error('Could not find result URL in output');
}

// Get all input items
const inputData = $input.all().map(item => item.json);

// Merge all objects into one
const combined = inputData.reduce((acc, curr) => {
  return { ...acc, ...curr };
}, {});

// Return the merged object
return [
  {
    json: combined
  }
];

// Get the raw string inside the output field
const rawOutput = $input.first().json.output;

// Remove ```json and ``` if present, and trim whitespace
const cleaned = rawOutput
  .replace(/```json|```/g, '')
  .trim();

// Parse the cleaned JSON string
const parsed = JSON.parse(cleaned);

// Return subject and body
return [
  {
    json: {
      subject: parsed.subject,
      body: parsed.body
    }
  }
];
