let aiResponse = $json.output;

// Remove triple backticks and possible "json" label
aiResponse = aiResponse.replace(/```json|```/g, '').trim();

let analysis;

try {
  analysis = JSON.parse(aiResponse);
} catch (error) {
  // Fallback parsing if JSON is malformed
  analysis = {
    sentiment: 'Neutral',
    intent: 'Needs Info',
    urgency: 'Medium',
    nextAction: 'Email',
    summary: 'Could not parse AI response',
    priority: 'Warm'
  };
}

// Get the original lead data from Normalize Gmail node
const leadData = $node['Normalize Gmail Data'].json;

// Combine everything
const result = {
  ...leadData,
  ...analysis,
  analysisDate: new Date().toISOString(),
  needsFollowUp: analysis.nextAction !== 'No Action',
  isHighPriority: analysis.priority === 'Hot' || analysis.urgency === 'High'
};

return [{ json: result }];
