function formatIST(date) {
  const options = {
    timeZone: 'Asia/Kolkata',
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: false
  };
  const parts = new Intl.DateTimeFormat('en-GB', options).formatToParts(date);
  const lookup = {};
  parts.forEach(p => lookup[p.type] = p.value);
  return `${lookup.year}-${lookup.month}-${lookup.day} ${lookup.hour}:${lookup.minute}:${lookup.second} IST`;
}

for (const item of items) {
  let dateStr = item.json.date_input?.toLowerCase?.() || 'today';
  let timeStr = item.json.time_input || '17:00'; // default to 5 PM

  // Step 1: base date
  let baseDate = new Date();
  if (dateStr === 'tomorrow') {
    baseDate.setDate(baseDate.getDate() + 1);
  } else if (dateStr !== 'today') {
    baseDate = new Date(dateStr);
  }

  // Step 2: parse time
  const [timePart, ampm] = timeStr.trim().split(/ (AM|PM)/i);
  let [hours, minutes] = timePart.split(':').map(Number);
  if (ampm?.toLowerCase() === 'pm' && hours < 12) hours += 12;
  if (ampm?.toLowerCase() === 'am' && hours === 12) hours = 0;

  baseDate.setHours(hours, minutes || 0, 0, 0);

  // Step 3: set IST formatted times
  const startIST = formatIST(baseDate);
  const endIST = formatIST(new Date(baseDate.getTime() + 60 * 60 * 1000)); // +1 hour

  item.json.appointment_datetime_parsed = startIST;
  item.json.appointment_end_datetime = endIST;
}

return items;
