// ---------- config ----------
const MIN_SCORE = 0.60; // raise/lower to be stricter/looser
// ----------------------------

// Build job list from Airtable items
const jobListings = [];
if (Array.isArray(items)) {
  for (const item of items) {
    if (item?.json) {
      jobListings.push({
        id:          item.json.id || '',
        title:       item.json["Job Title"] || '',
        jobCode:     item.json["Job Code"] || '',
        description: item.json["Description"] || '',
        skills:      item.json["Skills Required"] || '',
        experience:  item.json["Experience Required"] || item.json["Experience Required (Years)"] || '',
        location:    item.json["Location"] || ''
      });
    }
  }
}

// Email subject from Gmail Trigger (Option A: Simplify OFF)
const emailNode = $node["Gmail Trigger"];
const email = emailNode?.json || {};
let subject = email.subject || email.Subject || '';

if (!subject && items?.[0]?.json) {
  subject = items[0].json.subject || items[0].json.Subject || '';
}

function extractJobTitle(subj) {
  const lower = (subj || '').toLowerCase();
  const patterns = [
    /application (?:for|to) (.*?)(?:position|role|job)?$/i,
    /(?:applying|apply) (?:for|to) (?:the )?(.*?)(?:position|role|job)?$/i,
    /interested in (?:the )?(.*?)(?:position|role|job)/i,
    /regarding (?:the )?(.*?)(?:position|role|job)/i,
    /^(.*?)(?:position|job|role|application)$/i,
  ];
  for (const p of patterns) {
    const m = lower.match(p);
    if (m?.[1]?.trim()?.length > 3) return m[1].trim();
  }
  const capRe = /([A-Z][a-z]+(?: [A-Z][a-z]+)+)/g;
  let best = '', m;
  while ((m = capRe.exec(subj || '')) !== null) if (m[0].length > best.length) best = m[0];
  if (best.length > 5) return best.toLowerCase();
  return lower
    .replace(/application for|applying for|regarding|re:|job application|position|role|job|application|interest in/g, '')
    .replace(/^for the |for a |for |the |a /g, '')
    .replace(/\s+/g, ' ')
    .trim();
}

// ---------- NEW similarity ----------
const STOP = new Set(['the','a','an','and','for','to','of','role','position','job','senior','jr','junior','lead','manager','developer','engineer']);
const PHRASE_REPL = [
  ['machine learning','ai'],
  ['artificial intelligence','ai'],
  ['quality assurance','qa'],
  ['full stack','fullstack'],
  ['front end','frontend'],
  ['back end','backend'],
];
const TOKEN_MAP = { // single-token synonyms
  ml: 'ai',
  sdet: 'qa',
  testing: 'qa',
  test: 'qa',
  sre: 'devops',
};

function normalizeTitle(s = '') {
  s = s.toLowerCase();
  for (const [a,b] of PHRASE_REPL) s = s.replace(new RegExp(a, 'g'), b);
  s = s.replace(/[\/,()\-]/g, ' ');
  s = s.replace(/\s+/g, ' ').trim();
  return s;
}

function canonicalTokens(s = '') {
  const t = normalizeTitle(s).split(' ').filter(Boolean);
  const out = [];
  for (const tok of t) {
    if (STOP.has(tok)) continue;
    out.push(TOKEN_MAP[tok] || tok);
  }
  // dedupe, preserve order
  return Array.from(new Set(out));
}

function jaccard(a, b) {
  const A = new Set(a), B = new Set(b);
  if (A.size === 0 && B.size === 0) return 1;
  const inter = new Set([...A].filter(x => B.has(x))).size;
  const uni = new Set([...A, ...B]).size;
  return uni ? inter / uni : 0;
}

function lev(a, b) {
  a = normalizeTitle(a); b = normalizeTitle(b);
  if (!a && !b) return 1;
  const T = Array(b.length + 1).fill(null).map(() => Array(a.length + 1).fill(null));
  for (let i = 0; i <= a.length; i++) T[0][i] = i;
  for (let j = 0; j <= b.length; j++) T[j][0] = j;
  for (let j = 1; j <= b.length; j++) {
    for (let i = 1; i <= a.length; i++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      T[j][i] = Math.min(T[j][i - 1] + 1, T[j - 1][i] + 1, T[j - 1][i - 1] + cost);
    }
  }
  const d = T[b.length][a.length];
  const maxLen = Math.max(a.length, b.length) || 1;
  return 1 - d / maxLen;
}

// final score = 70% token match + 30% string similarity
function smartSim(s1, s2) {
  const jac = jaccard(canonicalTokens(s1), canonicalTokens(s2));
  const levSim = lev(s1, s2);
  return 0.7 * jac + 0.3 * levSim;
}
// ------------------------------------

const jobKeywords = extractJobTitle(subject);

// guard: no keywords
if (!jobKeywords) {
  return [{
    json: {
      subject,
      extractedJobTitle: jobKeywords,
      matchFound: false,
      reason: "No extractable job title from subject"
    },
  }];
}

// find best match using smartSim
let best = null, bestScore = 0;
for (const j of jobListings) {
  const s = smartSim(jobKeywords, j.title);
  if (s > bestScore) { best = j; bestScore = s; }
}

// Return the match
if (best && bestScore >= MIN_SCORE) {
  return [{
    json: {
      subject,
      extractedJobTitle: jobKeywords,
      matchFound: true,
      jobMatch: {
        id: best.id,
        title: best.title,
        code: best.jobCode,
        description: best.description,
        skills: best.skills,
        experience: best.experience,
        location: best.location,
        confidence: bestScore
      }
    },
  }];
}

return [{
  json: {
    subject,
    extractedJobTitle: jobKeywords,
    matchFound: false,
    reason: best
      ? `Low confidence (${bestScore.toFixed(2)}) vs threshold ${MIN_SCORE}`
      : 'No jobs in list'
  },
}];

// PromptBuilder ‚Äî builds the LLM prompt + carries email body & job meta

const m   = items[0]?.json ?? {};
const job = m.jobMatch ?? {};

// helpers
function stripHtml(s = '') {
  return String(s)
    .replace(/<style[\s\S]*?<\/style>/gi, '')
    .replace(/<script[\s\S]*?<\/script>/gi, '')
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();
}
function firstStr(...vals) {
  for (const v of vals) if (typeof v === 'string' && v.trim()) return v.trim();
  return '';
}

// get Gmail payload (paired item first, then pinned/previous run as fallback)
const g1 = $item(0, 'Gmail Trigger')?.$json ?? {};
const g2 = (($items && $items('Gmail Trigger', 0)) || [])[0]?.json ?? {};

// build email body: prefer any body already carried on the item, else read from Gmail
let emailBody = firstStr(
  m.emailBody,
  g1.text, g1.textPlain,
  g1.html ? stripHtml(g1.html) : '',
  g1.textHtml ? stripHtml(g1.textHtml) : '',
  g1.textAsHtml ? stripHtml(g1.textAsHtml) : '',
  g1.snippet,
  g2.text, g2.textPlain,
  g2.html ? stripHtml(g2.html) : '',
  g2.textHtml ? stripHtml(g2.textHtml) : '',
  g2.textAsHtml ? stripHtml(g2.textAsHtml) : '',
  g2.snippet
);
emailBody = emailBody.slice(0, 5000); // keep prompt compact

// subject: prefer the one already on the item (from EmailMatcher), else Gmail
const subject = firstStr(m.subject, g1.subject, g2.subject);

// prompt
const prompt = `
You are a senior technical recruiter.

TASKS
1) Read the ATTACHED resume (primary source of truth).
2) Consider the email subject & body for extra context.
3) Evaluate fit against the JOB description.

Extract ONLY from the resume (do NOT infer from email headers):
- candidateName: full legal name as printed in the resume. If missing, return "".
- candidatePhone: best reachable phone (normalize to E.164 if possible; else raw). If missing, return "".

Scoring:
- score: integer 1‚Äì10 (10=excellent, 7‚Äì9=strong, 4‚Äì6=partial, 1‚Äì3=poor).
- explanation: one concise sentence under 40 words.

EMAIL
- Subject: ${subject}
- Body: ${emailBody}

JOB
- Title: ${job.title || ""}
- Code: ${job.code || ""}
- Description: ${job.description || ""}
- Skills Required: ${job.skills || ""}
- Experience Required: ${job.experience || ""}
- Location: ${job.location || ""}

Return STRICT JSON only (no prose, no markdown):
{
  "score": <1-10>,
  "explanation": "<under 40 words>",
  "candidateName": "<from resume or empty string>",
  "candidatePhone": "<E.164 if possible else raw or empty string>"
}
`.trim();

return [{ json: { prompt, jobMeta: job } }];

const matched = items.some(i => i.json?.matchFound === true || i.json?.jobMatch);
if (!matched) return [];


// Keep only real resume-ish attachments
const ALLOWED = new Set([
  'application/pdf',
]);


// --- helpers ---
function headerPick(g, key) {
  const v = g.headers?.[key] || g.headers?.[key.toLowerCase()];
  return v ? String(v).replace(/^[A-Za-z-]+:\s*/, '') : '';
}
function pickEmail(str) {
  const m = String(str || '').match(/[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}/i);
  return m ? m[0].toLowerCase() : '';
}
function pickName(str) {
  // "Display Name" <email@domain>
  const m = String(str || '').match(/^"?([^"<]+?)"?\s*<[^>]+>/);
  return m ? m[1].trim() : '';
}
function extractSender(g) {
  // 1) Preferred: rich shape
  const v0 = g.from?.value?.[0];
  const addr = v0?.address || '';
  const nm   = v0?.name || '';

  // 2) Text forms (rich shape has these too)
  const fromText = typeof g.from?.text === 'string' ? g.from.text : '';
  const replyTo  = headerPick(g, 'reply-to');
  const fromHdr  = headerPick(g, 'from') || fromText;
  const returnPath = headerPick(g, 'return-path');

  // Decide email
  const email = addr || pickEmail(fromHdr) || pickEmail(replyTo) || pickEmail(returnPath) || '';
  // Decide name
  let name = nm || pickName(fromHdr);
  if (!name && email) name = email.split('@')[0];

  return { email, name };
}

const out = [];
for (const item of items) {
  const g = item.json || {};
  const { email: fromEmail, name: fromName } = extractSender(g);

  // Prefer internalDate (epoch); else parse ISO "date"; else now
  const when =
    (g.internalDate ? Number(g.internalDate) : 0) ||
    (g.date ? Date.parse(g.date) : 0) ||
    Date.now();

  if (!item.binary) continue;
  for (const key of Object.keys(item.binary)) {
    const bin = item.binary[key];
    if (!bin?.data) continue;
    if (ALLOWED.size && !ALLOWED.has(bin.mimeType)) continue;

    out.push({
      json: {
        fileName: bin.fileName || 'resume',
        mimeType: bin.mimeType || 'application/octet-stream',
        fromEmail,
        fromName,
        internalDate: when,
        subject: g.subject || headerPick(g, 'subject') || ''
      },
      binary: { data: bin } // Downstream nodes use Binary Property: "data"
    });
  }
}
return out;

const inItems = items.map(i => i.json);

// 1) file object from FileUpload
const fileBlock = inItems.find(o => o.file?.uri)?.file;
if (!fileBlock?.uri) throw new Error('Missing Gemini file URI');

// 2) prompt/meta from PromptBuilder
const metaBlock = inItems.find(o => typeof o.prompt === 'string') || {};
const jm = metaBlock.jobMeta || {};

// 3) prefer the ORIGINAL attachment mimeType (from ReadyAttachment);
//    fallback to the FileUpload's mimeType; final fallback to octet-stream.
const realMime =
  (inItems.find(o => o.mimeType)?.mimeType) ||      // ReadyAttachment
  fileBlock.mimeType ||                             // FileUpload (if correct)
  'application/octet-stream';

const parts = [];
if (metaBlock.prompt) parts.push({ text: metaBlock.prompt });
if (metaBlock.emailBody) parts.push({ text: `\nEMAIL BODY:\n${metaBlock.emailBody}` });

parts.push({
  text:
`JOB META:
Title: ${jm.title ?? ''}
Code: ${jm.code ?? ''}
Description: ${jm.description ?? ''}
Skills: ${jm.skills ?? ''}
Experience: ${jm.experience ?? ''}
Location: ${jm.location ?? ''}`
});

// ‚úÖ pass the real mime type here
parts.push({
  fileData: {
    fileUri: fileBlock.uri,
    mimeType: realMime
  }
});

const body = {
  contents: [{ role: 'user', parts }],
  generationConfig: {
    temperature: 0.1,
    responseMimeType: 'application/json',
    responseSchema: {
      type: 'object',
      properties: {
        score: { type: 'integer', minimum: 1, maximum: 10 },
        explanation: { type: 'string' },
        candidateName: { type: 'string' },
        candidatePhone: { type: 'string' }
      },
      required: ['score','explanation']
    },
  },
};

return [{ json: { body } }];

// Input is the ONE merged item
const j = items[0].json;

// Parse model JSON
const text = j?.candidates?.[0]?.content?.parts?.[0]?.text ?? '{}';
let data = {};
try { data = JSON.parse(text); } catch {}

const normPhone = s => s ? s.toString().trim().replace(/[^\d+]/g,'') : '';

// Build the exact columns we want for Sheets
return [{
  json: {
    JobTitle: j.jobMeta?.title || j.jobMatch?.title || j.subject || '',
    CandidateName: (data.candidateName || '').trim() || (j.fromName || ''),
    CandidateEmail: j.fromEmail || '',
    CandidatePhoneNumber: normPhone(data.candidatePhone || ''),
    // prefer open link; fallback to direct or ID
    ResumeLink: j.webViewLink || j.webContentLink || (j.id ? `https://drive.google.com/file/d/${j.id}/view?usp=drivesdk` : ''),
    AIScore: data.score ?? null,
    AIExplanation: data.explanation ?? ''
  }
}];

