  (function () {
    const email = ($json.fromEmail || 'unknown').toLowerCase();
    const user = email.split('@')[0].replace(/[^a-z0-9._-]+/g,'-').replace(/^-+|-+$/g,'');
    const d = new Date(Number($json.internalDate) || Date.now());
    const yyyy=d.getUTCFullYear(), mm=String(d.getUTCMonth()+1).padStart(2,'0'),
          dd=String(d.getUTCDate()).padStart(2,'0'),
          hh=String(d.getUTCHours()).padStart(2,'0'),
          mi=String(d.getUTCMinutes()).padStart(2,'0'),
          ss=String(d.getUTCSeconds()).padStart(2,'0');
    const ext = (($json.fileName || '').split('.').pop()
              || ($json.mimeType==='application/pdf'?'pdf':'bin')).toLowerCase();
    return `${user || 'unknown'}__${yyyy}-${mm}-${dd}-${hh}${mi}${ss}.${ext}`;
  })()
}}
