const data = $json.data;

function extractTextWithHeaders(html) {
  let cleanedHTML = html
    .replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, '')
    .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, '');

  cleanedHTML = cleanedHTML.replace(/<(h[1-6])[^>]*>(.*?)<\/\1>/gi, (match, tag, content) => {
    return `\n\n[${tag.toUpperCase()}] ${content.trim()} [/${tag.toUpperCase()}]\n\n`;
  });

  cleanedHTML = cleanedHTML.replace(/<[^>]+>/g, '');

  cleanedHTML = cleanedHTML.replace(/\s+/g, ' ').trim();

  return cleanedHTML;
}

const extractedText = extractTextWithHeaders(data);

console.log(extractedText);

