const excessFunds = parseInt($('Check User\'s USDC Balance').first().json.response) - parseInt($('Savings Configs').first().json.moveToSavingsThreshold)

$input.first().json.excessFunds = excessFunds;

return $input.all();

const fundGap = parseInt($('Savings Configs').first().json.topOffBalance) - parseInt($('Check User\'s USDC Balance').first().json.response)

// withdraw enough saving to put us halfway between the upper and lower balance settings
// the amount must be an Integer, so floor it to be sure
let withdrawAmount = Math.floor((parseInt($('Savings Configs').first().json.moveToSavingsThreshold) - parseInt($('Savings Configs').first().json.topOffBalance))/2) + fundGap

// if there are not enough savings, move as much as you can
if (withdrawAmount > parseInt($('Check User\'s Aave Savings').first().json.response)) {
  withdrawAmount = parseInt($('Check User\'s Aave Savings').first().json.response)
}

$input.first().json.fundGap = fundGap;
$input.first().json.withdrawAmount = withdrawAmount;


return $input.all();

// Any USDC balance above this gets stored in Aave
const moveToSavingsThreshold = 8564321; // set limit in atomic units (decimals)

// If your balance drops below this, move funds from Aave into your account
// IMPORTANT: this value must be less that moveToSavingsThreshold
const topOffBalance = 8123456; // set limit in atomic units (decimals)

const delegator = "0x9fead8b19c044c2f404dac38b925ea16adaa2954"; // your delegated wallet address.
const token = "0x833589fcd6edb6e08f4c7c32d4f71b54bda02913" // USDC on Base
const pool = "0xA238Dd80C259a72e81d7e4664a9801593F98d1c5" // L2Pool on Base
const telegramChatId = 5034284669; // the chat id between you and your Telegram bot

$input.first().json.moveToSavingsThreshold = moveToSavingsThreshold;
$input.first().json.topOffBalance = topOffBalance;
$input.first().json.delegator = delegator;
