const data = items.map(item => item.json);

const maxRowItem = data.reduce((max, current) => {
  return current.row_number > max.row_number ? current : max;
}, data[0]);

return [
  {
    json: maxRowItem
  }
];

const current = $json;
const previous = $('Calculate Bill').item.json;
const previousInfo = $('Find Latest Row').item.json;
const user = $('Prepare Data for Sheet').item.json.user;
const nama = $('Prepare Data for Sheet').item.json.Nama;

function formatRupiah(angka) {
  return angka.toString().replace(/\B(?=(\d{3})+(?!\d))/g, '.');
}

function removeLeadingZeros(str) {
  return str.toString().replace(/^0+/, '');
}

const volPrevlong = Number(previous['Volume Sebelumnya']);
const volPrev = Number(current['Volume Sebelumnya']);
const volumeSelisihlalu = Math.abs(volPrevlong - volPrev);
const volNowRaw = current['Volume Saat Ini'];
const volNow = Number(volNowRaw);
const volumeSelisih = Math.abs(volNow - volPrev);

const volNowDisplay = removeLeadingZeros(volNowRaw);

const totalBayarNow = formatRupiah(current['Total Bayar']);
const totalBayarPrev = formatRupiah(previousInfo['Total Bayar']);

return [
  {
    json: {
      user,
      nama,
      tanggal_sebelumnya: previousInfo['Tanggal Input'],
      tanggal_sekarang: current['Tanggal Input'],
      vol_sebelumnyalagi: volPrevlong,
      vol_sebelumnya: volPrev,
      vol_saat_ini: volNowDisplay,
      volume_selisihlalu: volumeSelisihlalu,
      volume_selisih: volumeSelisih,
      total_bayar_sekarang: `Rp${totalBayarNow}`,
      total_bayar_sebelumnya: `Rp${totalBayarPrev}`
    }
  }
];

const item = $json;

const volumeSebelumnya = Number(item["Volume Saat Ini"] || 0);
const volumeSekarang = String($('Image Explainer').first().json.output.m3).padStart(5, "0");
const hargaPerM3 = Number(item["Harga/m³"] || 0);
const beban = Number(item["Beban"] || 0);
const nama = item["Nama"] || "";

const volume = volumeSekarang - volumeSebelumnya;
const jumlahBayar = volume * hargaPerM3;
const totalBayar = jumlahBayar + beban;

const inputDate = $now;

const dateObj = new Date(inputDate);

const hari = ['Minggu', 'Senin', 'Selasa', 'Rabu', 'Kamis', 'Jumat', 'Sabtu'];
const bulan = ['Januari', 'Februari', 'Maret', 'April', 'Mei', 'Juni', 'Juli', 'Agustus', 'September', 'Oktober', 'November', 'Desember'];

const namaHari = hari[dateObj.getDay()];
const tanggal = dateObj.getDate();
const namaBulan = bulan[dateObj.getMonth()];
const tahun = dateObj.getFullYear();

const formatted = `${namaHari}, ${tanggal} ${namaBulan} ${tahun}`;

return [{
  json: {
    ...item,
    VolumeSaatini: volumeSekarang,
    Volume: volume,
    "Jumlah Bayar": jumlahBayar,
    "Total Bayar": totalBayar,
    caption: nama,
    tanggalF: formatted,
    mess_id: $('Telegram Trigger').first().json.message.message_id,
    chat_id: $('Telegram Trigger').first().json.message.chat.id,
    user: $('Telegram Trigger').first().json.message.from.first_name
  }
}];
