// n8n Code Node - Split long messages for Discord
// Place this node between AI Agent and Discord Send Message

const input = $input.all();
const results = [];

// Discord message limit (leaving some buffer)
const DISCORD_MAX_LENGTH = 1900;

// Process each input item
for (const item of input) {
  const longMessage = item.json.output || '';

  // If message is short enough, send as-is
  if (longMessage.length <= DISCORD_MAX_LENGTH) {
    results.push({
      json: {
        message: longMessage,
        messageNumber: 1,
        totalMessages: 1
      }
    });
    continue;
  }

  // Split long message into chunks
  const chunks = splitMessage(longMessage, DISCORD_MAX_LENGTH);

  // Create output items for each chunk
  chunks.forEach((chunk, index) => {
    results.push({
      json: {
        message: chunk,
        messageNumber: index + 1,
        totalMessages: chunks.length
      }
    });
  });
}

// Function to split message intelligently
function splitMessage(text, maxLength) {
  const chunks = [];
  let currentChunk = '';

  // Split by natural break points (double newlines, then single newlines, then sentences)
  const paragraphs = text.split('\n\n');

  for (const paragraph of paragraphs) {
    // If adding this paragraph would exceed limit
    if ((currentChunk + '\n\n' + paragraph).length > maxLength) {
      // Save current chunk if it has content
      if (currentChunk.trim()) {
        chunks.push(currentChunk.trim());
        currentChunk = '';
      }

      // If single paragraph is too long, split by sentences
      if (paragraph.length > maxLength) {
        const sentences = paragraph.split('. ');

        for (let i = 0; i < sentences.length; i++) {
          const sentence = sentences[i] + (i < sentences.length - 1 ? '. ' : '');

          if ((currentChunk + sentence).length > maxLength) {
            if (currentChunk.trim()) {
              chunks.push(currentChunk.trim());
              currentChunk = sentence;
            } else {
              // Even single sentence is too long, force split
              chunks.push(sentence.substring(0, maxLength - 3) + '...');
              currentChunk = '...' + sentence.substring(maxLength - 3);
            }
          } else {
            currentChunk += sentence;
          }
        }
      } else {
        currentChunk = paragraph;
      }
    } else {
      // Add paragraph to current chunk
      if (currentChunk) {
        currentChunk += '\n\n' + paragraph;
      } else {
        currentChunk = paragraph;
      }
    }
  }

  // Add final chunk
  if (currentChunk.trim()) {
    chunks.push(currentChunk.trim());
  }

  return chunks;
}

return results;

// n8n Code Node - Split comparison messages that are already formatted
// Place this node after AI Agent and before Discord Send Message

const input = $input.all();
const results = [];

for (const item of input) {
  const fullText = item.json.text || item.json.output || '';

  if (!fullText) {
    console.log('No text content found');
    continue;
  }

  // Split by the message delimiter pattern
  const messagePattern = /--- \(Message \d+\/\d+\) ---/;
  const chunks = fullText.split(messagePattern);

  // Clean up chunks and filter out empty ones
  const cleanChunks = chunks
    .map(chunk => chunk.trim())
    .filter(chunk => chunk.length > 0);

  // Create output for each chunk
  cleanChunks.forEach((chunk, index) => {
    results.push({
      json: {
        message: chunk,
        messageNumber: index + 1,
        totalMessages: cleanChunks.length,
        originalLength: fullText.length,
        chunkLength: chunk.length
      }
    });
  });

  // If no message delimiters found, treat as single message
  if (cleanChunks.length === 0 || cleanChunks.length === 1) {
    // Check if message is too long for Discord (over 1900 chars)
    if (fullText.length > 1900) {
      // Split into smaller chunks
      const smallChunks = splitLongMessage(fullText, 1800);

      smallChunks.forEach((chunk, index) => {
        results.push({
          json: {
            message: chunk,
            messageNumber: index + 1,
            totalMessages: smallChunks.length,
            originalLength: fullText.length,
            chunkLength: chunk.length,
            autoSplit: true
          }
        });
      });
    } else {
      // Single message that fits Discord limits
      results.push({
        json: {
          message: fullText,
          messageNumber: 1,
          totalMessages: 1,
          originalLength: fullText.length,
          chunkLength: fullText.length
        }
      });
    }
  }
}

