// n8n Code Node - Extract URLs for comparison
// This version handles messages like "compare, https://site1.com, https://site2.com"

const input = $input.all();
const results = [];

// URL regex pattern
const urlPattern = /https?:\/\/[^\s,]+/g;

for (const item of input) {
  const content = item.json.content || '';

  // Check if this is a comparison request
  const isComparison = content.toLowerCase().includes('compare');

  // Extract URLs
  const urls = content.match(urlPattern) || [];

  // Clean URLs
  const cleanUrls = urls.map(url => url.replace(/[,.\s]+$/, ''));

  if (cleanUrls.length > 0) {
    if (isComparison && cleanUrls.length > 1) {
      // For comparison, create one output with url1 and url2
      results.push({
        json: {
          type: 'comparison',
          url1: cleanUrls[0],
          url2: cleanUrls[1] || null,
          url3: cleanUrls[2] || null, // In case there are more than 2 URLs
          urlCount: cleanUrls.length,
          originalContent: content
        }
      });
    } else {
      // For single URL analysis, use url1
      results.push({
        json: {
          type: 'single',
          url1: cleanUrls[0],
          url2: null,
          urlCount: cleanUrls.length,
          originalContent: content
        }
      });
    }
  }
}

return results;
