parameters.jsCode:
const items = $input.all();
const urlRegex = /(https?:\/\/[^\s]+)/g;

const urls = items
  .map((item) => {
    const text = item?.json?.message?.text;
    const urlMatch = text.match(urlRegex);
    return urlMatch ? { url: urlMatch[0] } : null;
  })
  .filter((item) => item !== null);

return urls;
