// On r√©cup√®re tous les items en entr√©e sous forme de tableau.
// Dans votre cas, ce sera un tableau avec un seul item.
const items = $items();

// On utilise .map() pour cr√©er un nouveau tableau avec les items modifi√©s.
// C'est la m√©thode la plus propre et la plus s√ªre.
const newItems = items.map(item => {
  // On r√©cup√®re les donn√©es JSON de l'item en cours
  const json = item.json;

  // On r√©cup√®re le texte brut g√©n√©r√© par l'IA, qui est dans le champ 'output'
  const rawText = json.output;

  // On initialise une variable pour le texte format√©
  let formattedText = "";

  // On v√©rifie que le texte existe bien avant de le traiter
  if (rawText && typeof rawText === 'string') {
    const sentences = rawText.match(/[^.!?]+[.!?]*/g) || [];
    formattedText = sentences
      .map(s => s.trim())
      .filter(s => s.length > 0)
      .join('\n\n');
  }

  // On retourne un NOUVEL objet item, format√© comme n8n l'attend.
  // On copie toutes les anciennes donn√©es (...json) et on ajoute notre nouveau champ.
  return {
    json: {
      ...json,
      formatted_text: formattedText
    }
  };
});

// On retourne le nouveau tableau d'items. C'est la sortie attendue par n8n.
return newItems;
