const subject = $json.subject ? $json.subject.toLowerCase() : "";

let category = "unknown";

if (/resume|cv|job/.test(subject)) {
  category = "hr";
} else if (/invoice|bill/.test(subject)) {
  category = "billing";
} else if (/complaint|problem|issue/.test(subject)) {
  category = "complaint";
} else if (/feedback|suggestion/.test(subject)) {
  category = "feedback";
} else if (/inquiry|open|working|tomorrow|holiday/.test(subject)) {
  category = "inquiry";
} else if (/sales|offer|quotation/.test(subject)) {
  category = "sales";

}

return {
  json: {
    ...$json,
    category
  }
};

