let raw = $json.message?.content || $json.message || "";

// Remove triple backticks (```json ... ```)
raw = raw.replace(/```json|```/g, '').trim();

// Fix smart quotes and other formatting problems
raw = raw.replace(/[‚Äú‚Äù]/g, '"').replace(/[‚Äò‚Äô]/g, "'");

try {
  const parsed = JSON.parse(raw);


  return {
    summary: parsed.summary || "No summary provided",
    urgency: parsed.urgency || "Unknown",
    category: parsed.category || "Unknown",
    intent: parsed.intent || "Unknown",
    messgae_id: $('Get many messages').item.json.id,
    label_id: $("Get many labels").all().find((item) => item.json.name === `AI Agent/${parsed.intent}`).json.id
  };
} catch (err) {
  return {
    summary: "‚ö†Ô∏è Failed to parse GPT output",
    urgency: "Unknown",
    category: "Unknown",
    intent: "Unknown",
    error: err.message,
    original: raw
  };
}

const HOURS = 0.5; // hours window
const now = new Date();

return items.filter(item => {
  const timestamp = item.json.TimeStamp || item.json.timestamp || "";
  const urgency = (item.json.Urgency || item.json.urgency || "").toLowerCase();

  if (!timestamp) return false;

  const itemDate = new Date(timestamp);
  const hoursDiff = Math.abs((now - itemDate) / 36e5); // 36e5 = 60*60*1000

  console.log(hoursDiff)

  return hoursDiff <= HOURS && (urgency === "low" || urgency === "medium");
});

let text = ``;

const medium = [];
const low = [];

items.forEach(item => {
  const urgency = (item.json.Urgency || "").toLowerCase();
  const intent = (item.json.Intent || "").toLowerCase();
  if (urgency === "medium") {
    medium.push(item);
  } else if (
    urgency === "low" &&
    intent !== "Marketing"
  ) {
    low.push(item);
  }
});

if (medium.length === 0 && low.length === 0) {
  text += "_No important messages today._";
} else {
  if (medium.length > 0) {
    text += "*Medium Urgency:*\n";
    medium.forEach((item, index) => {
      text += `*${index + 1}.* ${item.json.Summary || "No summary"}\nFrom: ${item.json.From || "Unknown"}\nUrgency: ${item.json.Urgency || "Unknown"}, Category: ${item.json.Category || "Unknown"}, Intent: ${item.json.Intent || "N/A"}\n\n`;
    });
  }
  if (low.length > 0) {
    text += "*Low Urgency:*\n";
    low.forEach((item, index) => {
      text += `*${index + 1}.* ${item.json.Summary || "No summary"}\nFrom: ${item.json.From || "Unknown"}, Intent: ${item.json.Intent || "N/A"}\n\n`;
    });
  }
}

return [{ json: { text } }];
