const raw = $json.candidates[0].content.parts[0].text;
const match = raw.match(/```json\s*([\s\S]*?)\s*```/);
let body   = (match ? match[1] : raw)
	           .replace(/[\u0000-\u001F\u007F-\u009F]/g, '')
	           .replace(/,\s*([}\]])/g, '$1');

let data;
try {
	data = JSON.parse(body);            // ← will throw a readable n8n error if still bad
} catch (err) {
	throw new Error('LLM returned invalid JSON ➜ ' + err.message);
}

// Normalise: array of rows no matter what
if (!Array.isArray(data)) data = data.transactions || [data];
return data.map(t => ({ json: t }));
