// Fonction pour traiter et grouper les données Google Ads (ECOMMERCE uniquement)
function processGoogleAdsData(inputData) {
  // Vérifier si nous avons la structure attendue: inputData[0].json.results
  if (!inputData || !Array.isArray(inputData) || !inputData[0] || !inputData[0].json || !inputData[0].json.results) {
    console.log("Structure non reconnue:", JSON.stringify(inputData).substring(0, 300));
    return {};
  }

  const results = inputData[0].json.results;

  // Initialiser tous les types de campagnes avec des valeurs à 0
  const campaignTypes = {
    'PERFORMANCE_MAX': { cout: 0, conversion: 0 },
    'SEARCH': { cout: 0, conversion: 0 },
    'DISPLAY': { cout: 0, conversion: 0 },
    'VIDEO': { cout: 0, conversion: 0 },
    'DEMAND_GEN': { cout: 0, conversion: 0 },
    'SHOPPING': { cout: 0, conversion: 0 }
  };

  // Grouper les données par type de campagne
  const groupedData = {};

  results.forEach(result => {
    const channelType = result.campaign.advertisingChannelType;
    const costEuros = parseFloat(result.metrics.costMicros) / 1000000;
    const conversionsValue = parseFloat(result.metrics.conversionsValue || 0); // Chercher conversionsValue dans les données

    if (!groupedData[channelType]) {
      groupedData[channelType] = {
        totalCost: 0,
        totalConversionsValue: 0
      };
    }

    groupedData[channelType].totalCost += costEuros;
    groupedData[channelType].totalConversionsValue += conversionsValue;
  });

  // Remplir les données pour chaque type de campagne
  Object.keys(campaignTypes).forEach(type => {
    if (groupedData[type]) {
      campaignTypes[type].cout = Math.round(groupedData[type].totalCost * 100) / 100;
      campaignTypes[type].conversion = Math.round(groupedData[type].totalConversionsValue * 100) / 100; // Valeur en euros
    }
    // Les valeurs restent à 0 si pas de données pour ce type
  });

  return campaignTypes;
}

// Récupérer les données d'entrée
const inputData = $input.all();
const campaignData = processGoogleAdsData(inputData);

// Retourner les données dans le format demandé
return { 
  json: {
    Performance_max: campaignData.PERFORMANCE_MAX,
    Search: campaignData.SEARCH,
    Display: campaignData.DISPLAY,
    Video: campaignData.VIDEO,
    Demand_gen: campaignData.DEMAND_GEN,
    Shopping: campaignData.SHOPPING
  } 
};

