// Fonction pour obtenir la colonne correspondant au mois précédent
function getPreviousMonthColumn() {
  const now = new Date();
  const currentMonth = now.getMonth(); // 0 = Janvier, 1 = Février, etc.

  // Calculer le mois précédent
  let previousMonth = currentMonth - 1;

  // Si on est en janvier (0), le mois précédent est décembre (11)
  if (previousMonth < 0) {
    previousMonth = 11;
  }

  // Mapping des mois vers les colonnes (0-indexed)
  const monthToColumn = {
    0: 'B',  // Janvier
    1: 'C',  // Février
    2: 'D',  // Mars
    3: 'E',  // Avril
    4: 'F',  // Mai
    5: 'G',  // Juin
    6: 'H',  // Juillet
    7: 'I',  // Août
    8: 'J',  // Septembre
    9: 'K',  // Octobre
    10: 'L', // Novembre
    11: 'M'  // Décembre
  };

  return monthToColumn[previousMonth];
}

