// Lead Qualification & Scoring Logic
const lead = $input.first().json;
const enrichment = lead.enrichment_data || {};

// Initialize score
let score = 0;
let qualificationNotes = [];

// Company Size Scoring
if (enrichment.company?.metrics?.employees) {
  const employees = enrichment.company.metrics.employees;
  if (employees >= 500) {
    score += 30;
    qualificationNotes.push('Large company (500+ employees)');
  } else if (employees >= 50) {
    score += 20;
    qualificationNotes.push('Medium company (50-500 employees)');
  } else if (employees >= 10) {
    score += 10;
    qualificationNotes.push('Small company (10-50 employees)');
  }
}

// Industry Scoring
const targetIndustries = ['Technology', 'Software', 'SaaS', 'Finance', 'Healthcare'];
if (enrichment.company?.category?.industry) {
  if (targetIndustries.includes(enrichment.company.category.industry)) {
    score += 25;
    qualificationNotes.push(`Target industry: ${enrichment.company.category.industry}`);
  }
}

// Title/Role Scoring
if (enrichment.person?.employment?.title) {
  const title = enrichment.person.employment.title.toLowerCase();
  if (title.includes('ceo') || title.includes('founder') || title.includes('president')) {
    score += 25;
    qualificationNotes.push('C-level executive');
  } else if (title.includes('director') || title.includes('vp') || title.includes('manager')) {
    score += 15;
    qualificationNotes.push('Management level');
  }
}

// Email Domain Scoring
const email = lead.email || '';
const domain = email.split('@')[1];
const personalDomains = ['gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com'];
if (!personalDomains.includes(domain)) {
  score += 10;
  qualificationNotes.push('Business email domain');
} else {
  qualificationNotes.push('Personal email domain');
}

// Revenue Scoring (if available)
if (enrichment.company?.metrics?.annualRevenue) {
  const revenue = enrichment.company.metrics.annualRevenue;
  if (revenue >= 10000000) { // $10M+
    score += 20;
    qualificationNotes.push('High revenue company ($10M+)');
  } else if (revenue >= 1000000) { // $1M+
    score += 10;
    qualificationNotes.push('Medium revenue company ($1M+)');
  }
}

// Determine qualification level
let qualificationLevel;
let priority;
if (score >= 80) {
  qualificationLevel = 'High';
  priority = 'Immediate Follow-up';
} else if (score >= 50) {
  qualificationLevel = 'Medium';
  priority = 'Follow-up within 24h';
} else if (score >= 25) {
  qualificationLevel = 'Low';
  priority = 'Add to nurture sequence';
} else {
  qualificationLevel = 'Unqualified';
  priority = 'Archive or reject';
}

// Return enriched lead data
return {
  ...lead,
  qualification: {
    score: score,
    level: qualificationLevel,
    priority: priority,
    notes: qualificationNotes,
    timestamp: new Date().toISOString()
  },
  enriched_company: enrichment.company || {},
  enriched_person: enrichment.person || {}
};
