// Determine if this is monthly or quarterly based on current date
const now = new Date();
const currentMonth = now.getMonth() + 1; // getMonth() returns 0-11

// Check if current month is a quarter start (Jan=1, Apr=4, Jul=7, Oct=10)
const isQuarterly = currentMonth % 3 === 1;

// Calculate date ranges
let startDate, endDate, period;

if (isQuarterly && [1, 4, 7, 10].includes(currentMonth)) {
  // Quarterly report - get previous quarter
  const quarterStartMonth = currentMonth - 3;
  startDate = new Date(now.getFullYear(), quarterStartMonth - 1, 1);
  if (quarterStartMonth <= 0) {
    startDate = new Date(now.getFullYear() - 1, quarterStartMonth + 12 - 1, 1);
  }
  endDate = new Date(now.getFullYear(), currentMonth - 1, 0); // Last day of previous month
  period = 'quarterly';
} else {
  // Monthly report - get previous month
  startDate = new Date(now.getFullYear(), currentMonth - 2, 1);
  if (currentMonth === 1) {
    startDate = new Date(now.getFullYear() - 1, 11, 1); // December of previous year
  }
  endDate = new Date(now.getFullYear(), currentMonth - 1, 0); // Last day of previous month
  period = 'monthly';
}

// Convert to Unix timestamps for Stripe API
const startTimestamp = Math.floor(startDate.getTime() / 1000);
const endTimestamp = Math.floor(endDate.getTime() / 1000);

return [{
  json: {
    startDate: startTimestamp,
    endDate: endTimestamp,
    period: period,
    startDateFormatted: startDate.toISOString().split('T')[0],
    endDateFormatted: endDate.toISOString().split('T')[0]
  }
}];

const data = $json;

// Format currency
const formatCurrency = (amount) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
  }).format(amount);
};

// Format percentage
const formatPercentage = (value) => {
  return `${value}%`;
};

// Create period title from metadata
const periodTitle = `Revenue Report - ${data.metadata.period}`;

// Build top customers text
let topCustomersText = '';
if (data.customers.topCustomers && data.customers.topCustomers.length > 0) {
  topCustomersText = data.customers.topCustomers.map((customer, index) => {
    const customerName = customer.customer === 'Unknown Customer' ? 
      'Unknown Customer' : 
      `Customer ${customer.customer.substring(4, 14)}...`; // Show more meaningful part of customer ID
    return `   ${index + 1}. ${customerName}: ${formatCurrency(customer.amount)}`;
  }).join('\n');
} else {
  topCustomersText = '   No customer data available';
}

// Build payment methods breakdown
let paymentMethodsText = '';
if (data.payments.paymentMethodBreakdown) {
  paymentMethodsText = Object.entries(data.payments.paymentMethodBreakdown)
    .map(([method, amount]) => `   • ${method.charAt(0).toUpperCase() + method.slice(1)}: ${formatCurrency(amount)}`)
    .join('\n');
}

// Build refund reasons text
let refundReasonsText = '';
if (data.refundAnalysis.refundReasons && Object.keys(data.refundAnalysis.refundReasons).length > 0) {
  refundReasonsText = Object.entries(data.refundAnalysis.refundReasons)
    .map(([reason, count]) => `   • ${reason.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}: ${count}`)
    .join('\n');
} else {
  refundReasonsText = '   No refunds in this period';
}

// Risk level summary
const riskSummary = `Low: ${data.payments.riskAnalysis.low} | Medium: ${data.payments.riskAnalysis.medium} | High: ${data.payments.riskAnalysis.high}`;

// Build the comprehensive Slack message
const slackMessage = `📊 **${periodTitle}**
📅 *Generated: ${new Date(data.metadata.dataProcessedAt).toLocaleDateString()}*

💰 **Financial Summary:**
• Total Revenue: ${formatCurrency(data.summary.totalRevenue)}
• Total Refunds: ${formatCurrency(data.summary.totalRefunds)}
• Net Revenue: ${formatCurrency(data.summary.netRevenue)}
• Average Transaction: ${formatCurrency(data.summary.averageTransactionValue)}

📈 **Growth Metrics:**
• Estimated MRR: ${formatCurrency(data.growth.estimatedMRR)}
• Estimated ARR: ${formatCurrency(data.growth.estimatedARR)}

📊 **Transaction Analysis:**
• Successful Transactions: ${data.summary.totalTransactions}
• Refund Transactions: ${data.summary.totalRefundTransactions}
• Refund Rate: ${formatPercentage(data.summary.refundRate)}
• Unique Customers: ${data.customers.uniqueCustomers}

🏆 **Top Customers by Revenue:**
${topCustomersText}

💳 **Payment Methods:**
${paymentMethodsText}

⚠️ **Risk Analysis:**
${riskSummary}

🔄 **Refund Analysis:**
• Refund Rate: ${formatPercentage(data.refundAnalysis.refundRate)}
**Refund Reasons:**
${refundReasonsText}

---
*Data processed: ${data.metadata.chargesAnalyzed} charges, ${data.metadata.refundsAnalyzed} refunds*
*Auto-generated from Stripe API*`;

return [{
  json: {
    text: slackMessage,
    // Include original data for reference
    originalData: data,
    // Additional Slack formatting options
    blocks: [
      {
        "type": "section",
        "text": {
          "type": "mrkdwn",
          "text": slackMessage
        }
      }
    ]
  }
}];

// Get all merged data from the merge node
const allData = $input.all();

// Separate charges and refunds based on the data structure
let charges = [];
let refunds = [];

// Process each item from the merged data
allData.forEach(item => {
  const data = item.json;

  // Check if this is a charge object
  if (data.object === 'charge') {
    charges.push(data);
  }
  // Check if this is refunds list object
  else if (data.object === 'list' && data.data) {
    // Extract refund objects from the list
    refunds = refunds.concat(data.data);
  }
  // Check if this is a single refund object
  else if (data.object === 'refund') {
    refunds.push(data);
  }
});

// Filter only successful/paid charges that are not refunded
const paidCharges = charges.filter(charge => 
  charge.status === 'succeeded' && 
  charge.paid === true &&
  charge.amount_refunded === 0 // This means not refunded
);

// Calculate total revenue from non-refunded charges
const totalRevenue = paidCharges.reduce((sum, charge) => {
  return sum + (charge.amount / 100); // Convert from cents to dollars
}, 0);

// Calculate total refunds amount
const totalRefundsAmount = refunds.reduce((sum, refund) => {
  return sum + (refund.amount / 100); // Convert from cents to dollars
}, 0);

// Calculate net revenue
const netRevenue = totalRevenue - totalRefundsAmount;

// Get customer revenue breakdown
const customerRevenue = {};
paidCharges.forEach(charge => {
  const customer = charge.customer || 'Unknown Customer';
  const amount = charge.amount / 100;
  customerRevenue[customer] = (customerRevenue[customer] || 0) + amount;
});

// Get top 3 customers by revenue
const topCustomers = Object.entries(customerRevenue)
  .sort(([,a], [,b]) => b - a)
  .slice(0, 3)
  .map(([customer, amount]) => ({ 
    customer, 
    amount: Math.round(amount * 100) / 100 
  }));

// Calculate payment method breakdown
const paymentMethodBreakdown = {};
paidCharges.forEach(charge => {
  const brand = charge.payment_method_details?.card?.brand || 'unknown';
  const amount = charge.amount / 100;
  paymentMethodBreakdown[brand] = (paymentMethodBreakdown[brand] || 0) + amount;
});

// Calculate average transaction value
const averageTransactionValue = paidCharges.length > 0 ? totalRevenue / paidCharges.length : 0;

// Get current date for period estimation
const currentDate = new Date();
const currentMonth = currentDate.getMonth() + 1;
const currentYear = currentDate.getFullYear();

// Simple MRR estimation (assuming monthly data)
const estimatedMRR = totalRevenue;
const estimatedARR = estimatedMRR * 12;

// Risk analysis based on risk scores
const riskAnalysis = {
  low: paidCharges.filter(c => c.outcome?.risk_score <= 20).length,
  medium: paidCharges.filter(c => c.outcome?.risk_score > 20 && c.outcome?.risk_score <= 50).length,
  high: paidCharges.filter(c => c.outcome?.risk_score > 50).length
};

// Refund reasons breakdown
const refundReasons = {};
refunds.forEach(refund => {
  const reason = refund.reason || 'no_reason_given';
  refundReasons[reason] = (refundReasons[reason] || 0) + 1;
});

return [{
  json: {
    // Summary metrics
    summary: {
      totalRevenue: Math.round(totalRevenue * 100) / 100,
      totalRefunds: Math.round(totalRefundsAmount * 100) / 100,
      netRevenue: Math.round(netRevenue * 100) / 100,
      totalTransactions: paidCharges.length,
      totalRefundTransactions: refunds.length,
      averageTransactionValue: Math.round(averageTransactionValue * 100) / 100,
      refundRate: paidCharges.length > 0 ? Math.round((refunds.length / paidCharges.length) * 100 * 100) / 100 : 0
    },

    // Growth metrics
    growth: {
      estimatedMRR: Math.round(estimatedMRR * 100) / 100,
      estimatedARR: Math.round(estimatedARR * 100) / 100
    },

    // Customer insights
    customers: {
      topCustomers: topCustomers,
      uniqueCustomers: Object.keys(customerRevenue).length
    },

    // Payment analysis
    payments: {
      paymentMethodBreakdown: paymentMethodBreakdown,
      riskAnalysis: riskAnalysis
    },

    // Refund analysis
    refundAnalysis: {
      refundReasons: refundReasons,
      refundRate: totalRevenue > 0 ? Math.round((totalRefundsAmount / totalRevenue) * 100 * 100) / 100 : 0
    },

    // Metadata
    metadata: {
      dataProcessedAt: new Date().toISOString(),
      chargesAnalyzed: charges.length,
      refundsAnalyzed: refunds.length,
      period: `${currentMonth}/${currentYear}`
    }
  }
}];
