const out = [];
let passed = 0;
const postsCount = $('Code').first().json.postsCount
const limit = postsCount && postsCount == 15 ? 2 : 3
for (const item of items) {
	if (passed >= limit) break;

	const tweets = Array.isArray(item.json.tweets) ? item.json.tweets : [];

	for (const tweet of tweets) {
		if (passed >= limit) break;

		out.push({ json: tweet });
		passed++;
	}
}

return out;
