const langMap = {
  English: "en",
  German: "de",
  Russian: "ru",
  Polish: "pl",
  Spanish: "es",
  Portuguese: "pt",
  French: "fr",
  Italian: 'it',
  Turkish: "tr",
  Arabic: "ar",
  Dutch: "nl"
};

const results = [];
console.log($input)
let counter = 1
let video_key = 0
$input.all().forEach(item => {
  const videos = item.json.Video;
  const languages = item.json["Destination Language"];

  videos.forEach(video => {
    languages.forEach(lang => {
      const video_binary = item.json.Video.length === 1 ? "Video" : ("Video_"+video_key);
      results.push({
        json: {
          ...video,
          language: langMap[lang] || lang, // fallback to original if not mapped
          submittedAt: item.json.submittedAt,
          formMode: item.json.formMode,
          binary_order: counter
        },
        binary: { 
          ["VIDEO_" + counter] : item.binary[video_binary]
        }
      });
      counter +=1
    });
    video_key +=1
  });
});

return results;
