// Convert the input data to a Buffer and create a binary file
const jsonData = $("Loop Over Data").all(); // Get JSON from previous node
const fileContent = Buffer.from(JSON.stringify(jsonData, null, 2)); // Convert JSON to string and then to Buffer

return [
  {
    json: {},
    binary: {
      data: {
        data: fileContent.toString('base64'), // Convert buffer to base64
        mimeType: 'application/json',
        fileName: 'data.json'
      }
    }
  }
];
