// Clean up the post content by removing original channel signature
if ($input.first().json.post) {
  let cleanPost = $input.first().json.post    
    .replaceAll($('Set Telegram Channels').first().json.channelSignature, '')
    .trim();

  $input.first().json.post = cleanPost;
}

return $input.all();

// Process photo URL from CSS style attribute
if ($('Extract Post Content').first().json.photo) {
  const styleAttr = $('Extract Post Content').first().json.photo;
  const urlMatch = styleAttr.match(/background-image:url\('([^']+)'\)/);

  if (urlMatch && urlMatch[1]) {
    $input.first().json.photo = urlMatch[1];
  } else {
    // Fallback: try to extract from quotes
    const arr = styleAttr.split("'");
    if (arr.length >= 2) {
      $input.first().json.photo = arr[arr.length - 2];
    }
  }
}

return $input.all();
