// HTML to Plain Text Converter for n8n Code Node
// This code takes HTML content and converts it to clean plain text

// Get the HTML content from the previous node
// Adjust the field name based on your input data structure
const htmlContent = $input.first().json.data

if (!htmlContent) {
  return [{ json: { error: "No HTML content found in input" } }];
}

// Function to strip HTML tags and decode HTML entities
function htmlToPlainText(html) {
  let text = html;

  // Remove script and style elements completely
  text = text.replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '');
  text = text.replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '');

  // Replace common block elements with line breaks
  text = text.replace(/<\/?(div|p|br|h[1-6]|li|tr)[^>]*>/gi, '\n');

  // Replace list items with bullet points
  text = text.replace(/<li[^>]*>/gi, '‚Ä¢ ');

  // Remove all remaining HTML tags
  text = text.replace(/<[^>]*>/g, '');

  // Decode common HTML entities
  const entityMap = {
    '&amp;': '&',
    '&lt;': '<',
    '&gt;': '>',
    '&quot;': '"',
    '&#39;': "'",
    '&apos;': "'",
    '&nbsp;': ' ',
    '&copy;': '¬©',
    '&reg;': '¬Æ',
    '&trade;': '‚Ñ¢',
    '&hellip;': '...',
    '&mdash;': '‚Äî',
    '&ndash;': '‚Äì'
  };

  for (const [entity, char] of Object.entries(entityMap)) {
    text = text.replace(new RegExp(entity, 'g'), char);
  }

  // Decode numeric HTML entities (e.g., &#123;)
  text = text.replace(/&#(\d+);/g, (match, dec) => {
    return String.fromCharCode(dec);
  });

  // Decode hex HTML entities (e.g., &#x1F;)
  text = text.replace(/&#x([0-9A-Fa-f]+);/g, (match, hex) => {
    return String.fromCharCode(parseInt(hex, 16));
  });

  // Clean up whitespace
  text = text.replace(/\n\s*\n/g, '\n\n'); // Remove excessive line breaks
  text = text.replace(/[ \t]+/g, ' '); // Replace multiple spaces/tabs with single space
  text = text.trim(); // Remove leading/trailing whitespace

  return text;
}

try {
  const plainText = htmlToPlainText(htmlContent);

  return [{
    json: {
      originalHtml: htmlContent,
      plainText: plainText,
      characterCount: plainText.length,
      wordCount: plainText.split(/\s+/).filter(word => word.length > 0).length,
      success: true
    }
  }];

} catch (error) {
  return [{
    json: {
      error: `Failed to convert HTML to text: ${error.message}`,
      originalHtml: htmlContent,
      success: false
    }
  }];
}

