// Get the input text from the previous node
const input = $json.markdown || $json.text || "";

let text = input.replace(/Car Insurance Policy\s*\d+/gi, "");

// Remove "Page X" markers
text = text.replace(/Page\s*\d+/gi, "");

// Replace --- dividers with a single newline
text = text.replace(/-{3,}/g, "\n");

// Decode & cleanup artifacts
text = text.replace(/&#x26;/g, "&");   // fix HTML entities
text = text.replace(/[‚ì§]/g, "-");      // replace bullet symbols with dashes

// Collapse whitespace
text = text.replace(/\n{2,}/g, "\n\n"); // keep paragraph breaks
text = text.replace(/[ \t]+/g, " ");    // collapse spaces

// Step 5: Trim
text = text.trim();

// Output for next node
return { json: { normalizedText: text } };
