const item = $input.item.json;

// Creiamo Set per rimuovere duplicati
const uniqueFileIds = Array.from(new Set(item.file_id));
const uniqueFileNames = Array.from(new Set(item.file_name));

// Ritorniamo un item con i valori univoci
return [
  {
    json: {
      unique_file_ids: uniqueFileIds,
      unique_file_names: uniqueFileNames
    }
  }
];

